javascript: (function () {
  var e = document.getElementsByTagName("img"),
    t = [];
  for (var n = 0; n < e.length; n++)
    !e[n].complete || (0 === e[n].naturalWidth && t.push(e[n].src));
  if (t.length > 0) {
    var r = document.createElement("div");
    (r.style.position = "fixed"),
      (r.style.top = "20px"),
      (r.style.left = "20px"),
      (r.style.zIndex = "9999"),
      (r.style.background = "#fff"),
      (r.style.padding = "10px"),
      (r.style.border = "1px solid #ccc"),
      (r.style.fontFamily = "Arial, sans-serif");
    var o = document.createElement("h3");
    (o.innerHTML = "Broken Images:"), r.appendChild(o);
    var i = document.createElement("textarea");
    (i.style.width = "50vw"),
      (i.style.minHeight = "200px"),
      (i.style.resize = "none"),
      (i.value = t.join("\\n")),
      r.appendChild(i);
    var a = document.createElement("button");
    (a.innerHTML = "Close"),
      a.addEventListener("click", function () {
        r.remove();
      }),
      r.appendChild(a),
      document.body.appendChild(r);
  } else alert("No broken images found!");
})();
