var nf = new Intl.NumberFormat("de-DE", {
  style: "currency",
  currency: "EUR",
  minimumFractionDigits: 2,
  maximumFractionDigits: 2
});
$("#rent-b2c").on("input", function () {
  var e = document.getElementById("rent-b2c").value;
  (e = parseFloat(e)),
    (document.getElementById("protect-b2c").value = nf.format(12 * e)),
    (document.getElementById("cost-b2c").value = nf.format(12 * e * 0.04084));
}),
  $(".input-comercial").on("input", function () {
    var e = document.getElementById("rent-comercial").value;
    (e = parseFloat(e)),
      (document.getElementById("protect-comercial").value = nf.format(12 * e)),
      (document.getElementById("cost-comercial").value = nf.format(
        12 * e * 0.04
      ));
  });
